-- ----------------------------
-- Procedure structure for AppMenampilkanRecentScan
-- ----------------------------
DROP PROCEDURE IF EXISTS `AppMenampilkanRecentScan`;
delimiter ;;
CREATE PROCEDURE `AppMenampilkanRecentScan`(IN `P_UserID` varchar(20),IN `P_TglAwal` date,IN `P_TglAkhir` date)
BEGIN
	
	
	SELECT CASE WHEN DAYNAME(TanggalScan)= 'Sunday' THEN DATE_FORMAT(TanggalScan, "Mingggu, %d/%M/%Y")
										WHEN DAYNAME(TanggalScan)= 'Monday' THEN DATE_FORMAT(TanggalScan, "Senin, %d/%M/%Y")
										WHEN DAYNAME(TanggalScan)= 'Tuesday' THEN DATE_FORMAT(TanggalScan, "Selasa, %d/%M/%Y")
										WHEN DAYNAME(TanggalScan)= 'Wednesday' THEN DATE_FORMAT(TanggalScan, "Rabu, %d/%M/%Y")
										WHEN DAYNAME(TanggalScan)= 'Thursday' THEN DATE_FORMAT(TanggalScan, "Kamis, %d/%M/%Y")
										WHEN DAYNAME(TanggalScan)= 'Friday' THEN DATE_FORMAT(TanggalScan, "Jumat, %d/%M/%Y")
										WHEN DAYNAME(TanggalScan)= 'Saturday' THEN DATE_FORMAT(TanggalScan, "Sabtu, %d/%M/%Y") END AS TanggalScan,ScanMasuk,Terlambat,ScanPulang,Lembur,Shift,Keterangan,KetPulang,RpPotonganTerlambat
	FROM attlog WHERE UserID = P_UserID AND TanggalScan BETWEEN P_TglAwal AND P_TglAkhir ORDER BY TanggalScan DESC;
	

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for AppMenampilkanScan
-- ----------------------------
DROP PROCEDURE IF EXISTS `AppMenampilkanScan`;
delimiter ;;
CREATE PROCEDURE `AppMenampilkanScan`(IN `P_UserID` VARCHAR(20))
BEGIN
	SET lc_time_names = 'id_ID';
	SELECT DatangID, DATE_FORMAT(TanggalScan,"%W, %d/%M/%Y") as Tanggal,TanggalScan,ScanMasuk,Terlambat,ScanPulang,Lembur,Shift,Keterangan,KetPulang
	FROM attlog WHERE  MONTH(TanggalScan) = MONTH(CURRENT_DATE())
AND YEAR(TanggalScan) = YEAR(CURRENT_DATE()) AND UserID = P_UserID AND ScanMasuk IS NOT NULL ORDER BY TanggalScan DESC LIMIT 6;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for AppRekapPertahun
-- ----------------------------
DROP PROCEDURE IF EXISTS `AppRekapPertahun`;
delimiter ;;
CREATE PROCEDURE `AppRekapPertahun`(IN `P_UserID` VARCHAR(20))
BEGIN

	-- Menghitung Cuti
	
	
	-- Mengambil Tanggal Awal Cuti
	SET  @Awal = (SELECT TglMulaiCuti FROM User WHERE UserID = P_UserID );
	
	
	
	SET @JumlahCuti = (SELECT SUM(JumlahCuti) FROM attlog WHERE UserID = P_UserID AND TanggalScan BETWEEN
	@Awal AND CURRENT_DATE());

	SET @SisaCuti = (SELECT (12 - @JumlahCuti));



SET @Off = (SELECT COUNT(`Status`)FROM attlog WHERE Status = 'OFF' AND UserID= P_UserID AND MONTH(TanggalScan) = MONTH(CURRENT_DATE())
AND YEAR(TanggalScan) = YEAR(CURRENT_DATE())) ;
SET @JmlMasukKantor = (SELECT COUNT(`DatangID`)FROM attlog WHERE ScanMasuk IS NOT NULL AND UserID = P_UserID AND MONTH(TanggalScan) = MONTH(CURRENT_DATE())
AND YEAR(TanggalScan) = YEAR(CURRENT_DATE())) ;


	
(SELECT SUM(JumlahTerlambat) AS JumTerlambat, SUM(PenandaTerlambat) AS PenandaTerlambat , SUM(JumlahLembur) AS JmlLembur, SUM(Lembur) AS TotalJamLembur ,@JumlahCuti ,@SisaCuti ,SUM(JumlahLibur) AS JumlahLibur , SUM(JumlahDinasLuar) AS JumlahDinasLuar, SUM(JumlahIzinTidakMasuk) AS JumlahIzinTidakMasuk, SUM(JumlahCutiKhusus) AS JumlahCutiKhusus, SUM(JumlahTidakAbsen) AS JumlahTidakAbsen, SUM(RpPotonganTerlambat) AS RpPotonganTerlambat, SUM(RpLembur) AS RpLembur, SUM(RpPotKembaliIstirahat) AS RpPotKembaliIstirahat, @Off , @JmlMasukKantor ,  SUM(JmlSakit) as JumlahSakit
FROM attlog
WHERE YEAR(TanggalScan) = YEAR(CURRENT_DATE())
AND UserID = P_UserID);


END
;;
delimiter ;

