-- ----------------------------
DROP FUNCTION IF EXISTS `hariToDay`;
delimiter ;;
CREATE FUNCTION `hariToDay`(hari VARCHAR(6))
 RETURNS varchar(10) CHARSET latin1
BEGIN
	#Routine body goes here...
	DECLARE dayn VARCHAR(10);
	
	CASE LOWER(hari)
		WHEN 'minggu' THEN
			SET dayn = 'Sunday';
		WHEN 'senin' THEN
			SET dayn = 'Monday';
		WHEN 'selasa' THEN
			SET dayn = 'Tuesday';
		WHEN 'rabu' THEN
			SET dayn = 'Wednesday';
		WHEN 'kamis' THEN
			SET dayn = 'Thursday';
		WHEN 'jumat' THEN
			SET dayn = 'Friday';
		WHEN 'sabtu' THEN
			SET dayn = 'Saturday';
		ELSE SET dayn = '';
	END CASE;


	RETURN dayn;
END
;;
