CREATE PROCEDURE `MenampilkanDetailUser`(IN `P_UserID` VARCHAR(20))
BEGIN
	
	-- Mengambil Tanggal Awal Cuti
	SET  @Awal = (SELECT TglMulaiCuti FROM User WHERE UserID = P_UserID );
	
	
	
	SET @JumlahCuti = (SELECT SUM(JumlahCuti) FROM attlog WHERE UserID = P_UserID AND TanggalScan BETWEEN
	@Awal AND CURRENT_DATE());

	SET @SisaCuti = (SELECT (12 - @JumlahCuti));
	
	SELECT a.UserID, a.Pass, a.Nama, a.Alamat, a.HP, b.Jabatan,c.NamaCabang, a.Status ,a.TampilkanLembur,a.TampilkanTerlambat,a.DeviceID,a.RoleID,c.NamaCabang,a.Posisi,
		a.TglMasuk,a.TglLahir,a.TglMulaiCuti,a.TglAwalKontrakPertama,a.TglKeluar,a.GroupID, b.Jabatan, a.KodeCabang,
		DATE_FORMAT(a.TglLahir, "%Y-%m-%d") as FTglLahir,
		DATE_FORMAT(a.TglMasuk, "%Y-%m-%d") as FTglMasuk,
		DATE_FORMAT(a.TglMulaiCuti, "%Y-%m-%d") as FTglMulaiCuti,
		DATE_FORMAT(a.TglAwalKontrakPertama, "%Y-%m-%d") as FTglAwalKontrakPertama,
		DATE_FORMAT(a.TglKeluar, "%Y-%m-%d") as FTglKeluar,
		DATE_FORMAT(a.TglLahir, "%d %M %Y") as FormatTglLahir,
		DATE_FORMAT(a.TglMasuk, "%d %M %Y") as FormatTglMasuk,
		DATE_FORMAT(a.TglMulaiCuti, "%d %M %Y") as FormatTglMulaiCuti, 
    DATE_FORMAT(a.TglAwalKontrakPertama, "%d %M %Y") as FormatTglAwalKontrakPertama, 
    DATE_FORMAT(a.TglKeluar, "%d %M %Y") as FormatTglKeluar,
		@SisaCuti as SisaCuti,
		Username
    FROM user a
		JOIN tblgrupjabatan b ON b.GroupID = a.GroupID
		JOIN cabang c ON c.KodeCabang= a.KodeCabang Where UserID=P_UserID;

END
