-- ----------------------------
-- Procedure structure for MencariTanggalLupaScanPulang
-- ----------------------------
DROP PROCEDURE IF EXISTS `MencariTanggalLupaScanPulang`;
delimiter ;;
CREATE PROCEDURE `MencariTanggalLupaScanPulang`(IN P_UserID VARCHAR(200),P_TglAwal DATE,P_TglAkhir Date)
BEGIN
	
	
	
	-- SELECT attlog.TanggalScan FROM attlog WHERE ScanPulang IS NULL ;

		-- SELECT tblkeluarkantor.TanggalScan FROM tblkeluarkantor ORDER BY tblkeluarkantor.JamKembali DESC LIMIT 1;
	
	SELECT 
	att.DatangID, att.TanggalScan, att.Shift, att.ScanMasuk, att.ScanPulang, a.valid_keluar, att.Status
	FROM attlog att
	LEFT JOIN (
		SELECT * FROM (
			SELECT 
				DatangID, TanggalScan,
				IF(COUNT(JamKeluar) = SUM(IF(JamKembali IS NULL, 0, 1)),1,0) valid_keluar
			FROM tblkeluarkantor
			WHERE TanggalScan >= P_TglAwal AND TanggalScan <= P_TglAkhir
			GROUP BY DatangID, TanggalScan
		) x 
	) a ON a.DatangID = att.DatangID AND a.TanggalScan = att.TanggalScan
	WHERE att.TanggalScan >= P_TglAwal AND att.TanggalScan <= P_TglAkhir
	AND att.UserID = P_UserID
	AND att.Status IS NULL
	AND att.Shift IS NOT NULL
	AND (a.valid_keluar = 1 OR a.valid_keluar IS NULL)
	AND att.ScanPulang IS NULL
	ORDER BY att.TanggalScan;


END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for MengambilDatangID
-- ----------------------------
DROP PROCEDURE IF EXISTS `MengambilDatangID`;
delimiter ;;
CREATE PROCEDURE `MengambilDatangID`(IN `P_UserID` VARCHAR(20))
BEGIN




SET @GetDatangID = (Select DatangID from attlog WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE ORDER BY DatangID DESC Limit 1);
		
		
SET @KeluarKantor = (SELECT JamKeluar FROM tblkeluarkantor WHERE DatangID = @GetDatangID ORDER by JamKeluar DESC LIMIT 1);

SET @KembaliKantor = (SELECT JamKembali FROM tblkeluarkantor WHERE DatangID = @GetDatangID ORDER by JamKeluar DESC LIMIT 1);
		
		-- Mengambil Kondisi Sudah Scan Masuk Dan Scan Pulang di hari trsbt untuk orang tersbut
SET @ScanPulang = (SELECT ScanPulang FROM attlog WHERE DatangID = @GetDatangID );


SET @NamaShift = ( SELECT CASE WHEN Shift= '1' THEN 'Pagi' 
						WHEN Shift = '2' THEN 'Siang' 
						WHEN Shift = '3' THEN 'Sore' END
        FROM attlog
        WHERE DatangID = @GetDatangID);
			
		
		
		-- Mengambil Datang ID Dan Mengambil Scan Pulang Sesuai Data Dia Pulang 
		 
	 SELECT DatangID, Shift,@ScanPulang,ScanMasuk, @NamaShift , IstirahatKeluar, IstirahatKembali, RpPotKembaliIstirahat,Keterangan,
	 CASE WHEN Shift = 1 THEN (SELECT b.JamPulang  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID  AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 2 THEN (SELECT b.JamPulangSiang  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 3 THEN (SELECT b.JamPulangSore  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  END AS JamPulang,
	CASE WHEN Shift = 1 THEN (SELECT b.MaxJamDatang FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID  AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 2 THEN (SELECT b.MaxJamDatangSiang  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 3 THEN (SELECT b.MaxJamDatangSore  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  END AS MaxJamDatang,
	CASE WHEN Shift = 1 THEN (SELECT b.MinJamLembur  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID  AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 2 THEN (SELECT b.MinJamLemburSiang  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 3 THEN (SELECT b.MinJamLemburSore  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  END AS MinJamLembur,
	CASE WHEN Shift = 1 THEN (SELECT b.JamMulaiPagi  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID  AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 2 THEN (SELECT b.JamMulaiSiang  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 3 THEN (SELECT b.JamMulaiSore  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  END AS JamMulaiIstirahat,
	CASE WHEN Shift = 1 THEN (SELECT b.MaxJamKembali  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID  AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 2 THEN (SELECT b.MaxJamKembaliSiang  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  WHEN Shift = 3 THEN (SELECT b.MaxJamKembaliSore  FROM attlog a JOIN tblgrupjabatan b ON a.GroupID = b.GroupID  WHERE UserID= P_UserID AND TanggalScan=CURRENT_DATE Limit 1)
  END AS JamSelesaiIstirahat,
	@KeluarKantor,@KembaliKantor
	
	
	 from attlog WHERE UserID= P_UserID AND ScanMasuk IS NOT NULL AND TanggalScan=CURRENT_DATE ORDER BY DatangID DESC Limit 1 ;

END
;;
delimiter ;

