CREATE PROCEDURE `ProsesAbsensi`(IN P_UserID VARCHAR(50),IN P_TanggalScan date)
BEGIN
## 
SET @Nama = (SELECT Nama FROM user where UserID =P_UserID);

## Set Jam Jadi Format Indonesia
		SET lc_time_names = 'id_ID';
## Mengecek GroupID
		SET @S_GroupID = ( SELECT GroupID FROM `user` WHERE UserID = P_UserID);
## Mengecek Kode Cabang
		SET @S_KC = ( SELECT KodeCabang FROM `user` WHERE UserID = P_UserID);
## Mengambil Hari Libur Dari User Tersebut
		SET @HariLibur = (SELECT HariLibur FROM tblgrupjabatan WHERE GroupID=@S_GroupID);
## Mengambil Nama Hari Libur
		SET @NamaHariLibur = (SELECT DATE_FORMAT(P_TanggalScan,"%W"));
## Mengecek Kondisi Libur / Tidak 
		SET @KondisiLibur = (SELECT IF(@HariLibur = @NamaHariLibur,1,0));
## Mengecek Jatah Cuti Masih Ada atau tidackk bosQ
		SET  @Awal = (SELECT TglMulaiCuti FROM User WHERE UserID = P_UserID );
		SET @JumlahCuti = (SELECT SUM(JumlahCuti) FROM attlog WHERE UserID = P_UserID AND TanggalScan BETWEEN
		@Awal AND CURRENT_DATE());
		SET @SisaCuti = (SELECT (12 - @JumlahCuti));
#Set Status scan 
		SET @S_Status = (SELECT IF((@SisaCuti > 0),'CUTI','TIDAK MASUK'));
## Mengeset Status Finalblow Ultimate 
		SET @StatusFinal = (SELECT IF((@KondisiLibur=1),'LIBUR',@S_Status));
## Kondisi Cuti / Tidak Memiliki Cuti
		SET @KondisiCuti = IF((@StatusFinal = 'CUTI'),1, 0);			
## Kondisi Tidak Masuk	
		SET @KondisiTidakMasuk = IF((@StatusFinal = 'TIDAK MASUK'),1, 0);	
#SET potongan Tidack Masuk
		SET @RpPotongan = ( SELECT (@KondisiTidakMasuk * RpPotonganTidakMasuk)
		FROM tblgrupjabatan WHERE  GroupID =@S_GroupID);
		SET @Keterangan = (SELECT IF((@KondisiLibur=1),'','Lupa Absen'));

	
	
		
		VALUES (P_UserID,@Nama, P_TanggalScan, @StatusFinal, @Keterangan, @S_KC , @S_GroupID, 
		@RpPotongan,@KondisiCuti,@KondisiLibur,@KondisiTidakMasuk); 

END
;;
