BEGIN

	-- Mencari User ID dari datng id yang dikirim
	
			SET @S_UserID = ( SELECT UserID FROM `attlog` WHERE DatangID = P_DatangID);
		
	-- Mencari Group ID 
			SET @S_GroupID = ( SELECT GroupID FROM `user` WHERE UserID = @S_UserID);
			

			
			
	-- Mencari Max JamKembali
		
			SET @MaxJamKembali = ( SELECT MaxJamKembali FROM `tblgrupjabatan` WHERE GroupID = @S_GroupID );
			
	-- SET Rp Potongan Terlambat 
	
			SET @RpPotongan = ( SELECT RpPotonganTerlambatKembali FROM `tblgrupjabatan` WHERE GroupID= @S_GroupID);
			
		-- KOndisi 1/0 Apakah KAryawan Tersebut Terlambat Kembali Atau Tidak 
	
		SET @Kondisi = IF((P_IstirahatKembali > @MaxJamKembali ),1,0);
		
			-- Mencari Selisih Waktu Kembali
		
		SET @Diff = TIMEDIFF(P_IstirahatKembali,@MaxJamkembali);
	
	-- Menghitung Jumlah Potongan 
		SET @TotPotongan = (SELECT (@Kondisi * @RpPotongan));		
		
		-- Proses Memasukkan Ke Database
		
	UPDATE attlog 
	SET IstirahatKembali = P_IstirahatKembali ,
	    TerlambatIstirahat = IF((P_IstirahatKembali > @MaxJamKembali), @Diff, NULL),
		  JmlTerlambatIstirahat = IF((P_IstirahatKembali > @MaxJamKembali), 1, NULL),
			KetIstirahatKembali = P_KetIstirahatKembali,
			RpPotKembaliIstirahat = @TotPotongan
	WHERE DatangID = P_DatangID ;		


END
;;
