delimiter ;;
CREATE PROCEDURE `ProsesMasuk`(IN `P_UserID` VARCHAR(20), IN `P_TanggalScan` DATE, IN `P_ScanMasuk` TIME, IN `P_Shift` INT(2), IN `P_KodeCabang` VARCHAR(20))
BEGIN
SET @Nama = (SELECT Nama FROM user where UserID =P_UserID);
-- Mencari Group ID Karyawan
			SET @S_GroupID = ( SELECT GroupID FROM `user` WHERE UserID = P_UserID);


-- Memilih Kapan Jam Datang Sesuai dengan shift yang dipilih karyawan 
        SET @jamDatang = (
        SELECT CASE WHEN P_Shift = '1' THEN JamDatang WHEN P_Shift = '2' THEN JamDatangSiang WHEN P_Shift = '3' THEN JamDatangSore END
        FROM tblgrupjabatan
        WHERE GroupID = @S_GroupID);
		
        SET @ruleTerlambatBertingkat = (SELECT CASE WHEN RuleTerlambatBertingkat ='true' THEN 'Y' ELSE 'N' END  as ruleTerlambatBertingkat FROM tblgrupjabatan
        WHERE GroupID = @S_GroupID);
       
				SET @MaxDatang = (
        SELECT CASE WHEN P_Shift = '1' THEN MaxJamDatang WHEN P_Shift = '2' THEN MaxJamDatangSiang WHEN P_Shift = '3' THEN MaxJamDatangSore END
        FROM tblgrupjabatan
        WHERE GroupID = @S_GroupID);
				
				set @MaxDatang1Minute = Addtime(@MaxDatang,"59");
				
				set @P_ScanMasuk1Minute = Addtime(P_ScanMasuk,"-00:00:59");
-- hitung selisih waktu untuk jscan masuk dan jam scan datang
        SET @diff = TIMEDIFF(P_ScanMasuk,@jamDatang);
				
				SET @jamterlambat = TIMEDIFF(P_ScanMasuk,@MaxDatang);
				
-- Kondisi Terlambat dengan toleransi menampilkan jumlah menit terlambat 
				SET @kondisi = IF((P_ScanMasuk > addtime(@MaxDatang, "59")),@jamterlambat, NULL);
				
-- Kondisi Terlambat dengan toleransi menampilkan jumlah menit terlambat yang dirubah dari Null ke 0 dan difff ke 1 untuk proses perkalian 
				SET @kondisi0 = IF((P_ScanMasuk > addtime(@MaxDatang,"59")),1, 0);			
				
-- Kondisi Terlambat dengan toleransi menampilkan value 1 / Null apakah user trsbt terlambat atau tidak				
				SET @kondisi0_1 =  IF((P_ScanMasuk > addtime(@MaxDatang, "59")), 1, NULL);
				

-- Hitung RP Potongan Terlambat Normal
				SET @PotNormal = ( SELECT (@kondisi0 * RpPotonganTerlambat)
				FROM tblgrupjabatan WHERE  GroupID =@S_GroupID) ;
		
		

				
-- Hitung potongan Terlambat Bertingkat
				SET @PotBertingkat =(SELECT RpPotonganTerlambat FROM tblruleterlambatbertingkat 
														WHERE @P_ScanMasuk1Minute  > MaxJamDatang AND 
														P_Shift = Shift and GroupID = @S_GroupID
														ORDER BY MaxJamDatang DESC LIMIT 1 );
														
-- Kondisi Terlambat Bertingkat kurang dari batas minimum

				SET @disting = IF ((@PotBertingkat IS NULL	), @PotNormal ,@PotBertingkat) ;															
										       
-- Hitung Final Potongan	
				SET @FinalPot = (SELECT IF ((@ruleTerlambatBertingkat='Y'),@disting , @PotNormal));
				
   -- proses memasukkan data ke dalam database

		Terlambat,
		JumlahTerlambat,
		PenandaTerlambat,	
		TotalTerlambatTanpaToleransi,
	  RpPotonganTerlambat ) 
		
		VALUES (P_UserID,@Nama, P_TanggalScan, P_ScanMasuk, P_Shift,P_KodeCabang, @S_GroupID, 
		@kondisi, 
		@kondisi0_1,
		IF(P_ScanMasuk>=@jamDatang, 1, NULL),
		IF(P_ScanMasuk>=@jamDatang, @diff, NULL),
		@FinalPot); 
END
;;
delimiter ;

