		TotalTerlambatTanpaToleransi,
	  RpPotonganTerlambat,
		KodeCabang ) 
		
		VALUES (P_UserID,@Nama, P_TanggalScan, P_ScanMasuk, P_Shift,P_Keterangan, @S_GroupID, 
		@kondisi, 
		@kondisi0_1,
		IF(P_ScanMasuk>=@jamDatang, 1, NULL),
		IF(P_ScanMasuk>=@jamDatang, @diff, NULL),
		@FinalPot,
		@KodeCabang); 

END
;;
delimiter ;

