DROP PROCEDURE IF EXISTS `ProsesPulang`;
delimiter ;;
CREATE PROCEDURE `ProsesPulang`(IN `P_DatangID` INT, IN `P_ScanPulang` TIME, IN `P_KetPulang` VARCHAR(250))
BEGIN

	-- Mencari User ID dari datng id yang dikirim
	
			SET @S_UserID = ( SELECT UserID FROM `attlog` WHERE DatangID = P_DatangID);
		
	-- Mencari Group ID 
			SET @S_GroupID = ( SELECT GroupID FROM `user` WHERE UserID = @S_UserID);
		
		
  -- Mencari Shift
			SET @Shift = (SELECT Shift FROM attlog WHERE UserID = @S_UserID AND TanggalScan=CURRENT_DATE LIMIT 1);
	
	-- Mencari JamPulang
			SET @JamPulang = (SELECT CASE WHEN @Shift = '1' THEN JamPulangSiang WHEN @Shift = '2' THEN JamPulangSore WHEN @Shift = '3'
				THEN JamPulang END
        FROM tblgrupjabatan
        WHERE GroupID = @S_GroupID);
	
	-- Menhitung Min Lembur
				SET @MinLembur = (
        SELECT CASE WHEN @Shift = '1' THEN MinJamLembur WHEN @Shift = '2' THEN MinJamLemburSiang WHEN @Shift = '3' THEN MinJamLemburSore END
        FROM tblgrupjabatan
        WHERE GroupID = @S_GroupID);
				
				
				SET @MulaiLembur = (
        SELECT CASE WHEN @Shift = '1' THEN JamMulaiLembur WHEN @Shift = '2' THEN JamMulaiLemburSiang WHEN @Shift = '3' THEN JamMulaiLemburSore END
        FROM tblgrupjabatan
        WHERE GroupID = @S_GroupID);
	
	-- MenhitungLembur
					SET @Lembur = TIMEDIFF(P_ScanPulang,@MulaiLembur);
		
	-- Menghitung Jam Lembur Pembulatan PErjam Kebawah
					SET @JamLemburJam =  IF(@Lembur > '13:00:00',13,
										 (IF(@Lembur > '12:00:00',12,
											(IF(@Lembur > '11:00:00',11,
											 (IF(@Lembur > '10:00:00',10,
												(IF(@Lembur > '09:00:00',9,
												 (IF(@Lembur > '08:00:00',8,
													(IF(@Lembur > '07:00:00',7,
													 (IF(@Lembur > '06:00:00',6,
														(IF(@Lembur > '05:00:00',5,
														 (IF(@Lembur > '04:00:00',4,
															(IF(@Lembur > '03:00:00',3,
															 (IF(@Lembur > '02:00:00',2,
																(IF(@Lembur > '01:00:00',1,
																(IF( P_ScanPulang > @MinLembur, 
																((@Lembur)/60),0
																 ))
																))
															 ))
															))
														 ))
														))
													 ))
													))
												 ))
												))
											 ))													
											))
										 ))
										);
		
		-- Menghitung Jam Lembur Permenit Rinci
				
					SET @JamLemburMenit = IF(( P_ScanPulang > @MinLembur), HOUR(@Lembur)+(MINUTE(@Lembur)/60),0);
																	
		-- Memasukkan Lembur (JAM ) Ke att log (SELECT HOUR(@Lembur));MINUTE(@Lembur)
		
		SET @LemburJam = IF((P_ScanPulang > @MinLembur), HOUR(@Lembur), NULL);
		SET @LemburMenit = IF((P_ScanPulang > @MinLembur), MINUTE(@Lembur), NULL);											 
										
					-- Set Nominal Lembur
	SET @RpLembur = (SELECT (@JamLemburMenit * RpLemburPerJam) as Lembur FROM tblgrupjabatan
	 WHERE GroupID = @S_GroupID) ;
		
	UPDATE attlog 
	SET ScanPulang = P_ScanPulang ,
	    Lembur = IF((P_ScanPulang > @MinLembur), @Lembur, NULL),
		  JumlahLembur = IF(( P_ScanPulang > @MinLembur), 1, NULL),
			KetPulang = P_KetPulang,
			RpLembur = @RpLembur,
			LemburJam = @LemburJam,
			LemburMenit = @LemburMenit
	WHERE DatangID = P_DatangID ;
	
END
;;
delimiter ;

