delimiter ;;
CREATE PROCEDURE `ReportPertanggal`(IN `P_UserID` varchar(20),IN `P_TglAwal` date,IN `P_TglAkhir` date)
BEGIN
#Menghitung Potongan 
-- Rp Potongan Terlambat 
 SET @RpPotonganTerlambat = (SELECT SUM(RpPotonganTerlambat) FROM attlog
															WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir AND UserID = P_UserID);
															
-- Rp Potongan TidakMasuk
 SET @RpPotonganTidakMasuk = (SELECT SUM(RpPotonganIzinTidakMasuk) FROM attlog
															WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir AND UserID = P_UserID);
															
-- Rp Potongan Terlambat Kembali Istirahat
 SET @RpPotonganIstirahat = (SELECT SUM(RpPotKembaliIstirahat) FROM attlog
															WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir AND UserID = P_UserID);
															
SET @TotalPotongan = (SELECT (@RpPotonganTerlambat + @RpPotonganTidakMasuk + @RpPotonganIstirahat ));
	
#############################################################

-- Jumlah Sakit
 SET @JumlahSakit = (SELECT SUM(JmlSakit) FROM attlog
															WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir AND UserID = P_UserID);

 -- Jumlah Cuti
 SET @JC = (SELECT SUM(JumlahCuti) FROM attlog
															WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir AND UserID = P_UserID);
															
	-- Jumlah Ijin Tidak Masuk
 SET @IjinTidakMasuk = (SELECT SUM(JumlahIzinTidakMasuk) FROM attlog
															WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir AND UserID = P_UserID);
															
SET @TotalTidakMasuk = (SELECT (@JC + @IjinTidakMasuk + @JumlahSakit ));												


#################################

SET @TglPrt = (SELECT CURRENT_TIMESTAMP);
SET @tglPrint = (SELECT CONCAT(@TglPrt,' - '));
###########

	
SET  @Awal = (SELECT TglMulaiCuti FROM User WHERE UserID = P_UserID );
	
	
	
SET @JumlahCuti = (SELECT SUM(JumlahCuti) FROM attlog WHERE UserID = P_UserID AND TanggalScan BETWEEN
@Awal AND CURRENT_DATE());

SET @SisaCuti = (SELECT (12 - @JumlahCuti));



	SET @Periode1 = (SELECT  DATE_FORMAT(P_TglAwal,"%W, %d/%m/%Y"));
	SET @Periode2 = (SELECT  DATE_FORMAT(P_TglAkhir,"%W, %d/%m/%Y"));
	
	SET @Periode = (SELECT CONCAT(@Periode1,'  s/d  ',@Periode2));
	
SET @Off = (SELECT COUNT(`Status`)FROM attlog WHERE Status = 'OFF' AND UserID= P_UserID AND TanggalScan BETWEEN P_TglAwal AND P_TglAkhir) ;


SET @JmlMasukKantor = (SELECT COUNT(`DatangID`)FROM attlog 
WHERE ScanMasuk IS NOT NULL AND UserID = P_UserID
AND TanggalScan BETWEEN P_TglAwal AND P_TglAkhir) ;

# Menghitung Lembur 


SET @LemburJam = (SELECT SUM(LemburJam) FROM attlog WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir AND UserID = P_UserID  ); 

SET @LemburMenit = (SELECT SUM(LemburMenit) FROM attlog WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir AND UserID = P_UserID  ); 

SET @ModMenitLembur = (SELECT (@LemburMenit % 60));
SET @DivMenit = (SELECT (@LemburMenit DIV 60));

SET @TotalLmbrJam = (SELECT (@LemburJam + @DivMenit));

SET @TotalJamLemburFix = (SELECT CONCAT(@TotalLmbrJam," Jam ",@ModMenitLembur, " Menit"));


	
(SELECT SUM(JmlIjinTerlambat) as IjinTerlambat,
SUM(JumlahTerlambat) AS JumTerlambat,
SUM(PenandaTerlambat) AS PenandaTerlambat ,
SUM(JumlahLembur) AS JumlahLembur,
@TotalJamLemburFix AS TotalJamLembur ,
@JC AS JumlahCuti ,
@JumlahCuti AS TotalCutiThnIni,
SUM(JumlahLibur) AS JumlahLibur ,
SUM(JumlahDinasLuar) AS JumlahDinasLuar,
@IjinTidakMasuk AS JumlahIzinTidakMasuk,
SUM(JumlahCutiKhusus) AS JumlahCutiKhusus,
SUM(JmlTerlambatIstirahat) AS TerlambatKembali,
SUM(JumlahTidakAbsen) AS JumlahTidakAbsen,
@JumlahSakit AS JumlahSakit,
@RpPotonganTerlambat as RpPotonganTerlambat ,
SUM(RpLembur) AS RpLembur,
@RpPotonganTidakMasuk AS RpPotonganTidakMasuk, 
@RpPotonganIstirahat AS RpPotKembaliIstirahat,
@TotalPotongan AS TotalPotongan,
@TotalTidakMasuk AS TotalTidakMasuk,
@SisaCuti AS SisaCuti,
@Off AS JumlahOFF,
@Periode as Periode,
@TglPrint as TglPrint,
@JmlMasukKantor AS JmlMasukKantor,
DATE_FORMAT(P_TglAwal,"%Y-%m-%d") AS TglAwal,
DATE_FORMAT(P_TglAkhir,"%Y-%m-%d") AS TglAkhir



FROM attlog
WHERE TanggalScan BETWEEN P_TglAwal AND P_TglAkhir
AND UserID = P_UserID);


END
;;
