INSERT INTO `news` VALUES (1, NULL, 'HEADLINE', 'WELLCOME MEMBER\",\"<p>SELAMAT BERGABUNG</p><p><br></p><p>Dear member,</p><p>Terimakasih sudah bergabung bersama kami, ini adalah langkah tepat untuk mengubah Absensi Anda.<br></p>', NULL, '2021-02-24', 'robbin', NULL, NULL, '2021-02-24', '2021-02-28', 'true', 'MIT');
INSERT INTO `news` VALUES (2, NULL, 'LIBUR', 'Tanggal 11 Mar 2021', '192.168.100.123/', '2021-02-24', 'robbin', NULL, NULL, '2021-03-01', '2021-03-10', 'true', 'MIT');
INSERT INTO `news` VALUES (3, 'SB2/2101/IMO/0013', 'Aturan Terkait Covid-19', 'Dengan Hormat,\r\n\r\nBersama ini kami memberitahukan terkait covid-19, maka: \r\n\r\n1.Seluruh karyawan yang di swab antigen hasilnya positif/ reaktif, maka ada 2 opsi:\r\n   A.Melakukan isolasi/karantina mandiri 14hari. \r\n       Apabila selesai masa karantina 14 hari dan dirasa tidak ada gejala apapun, maka yang \r\n       bersangkutan diwajibkan melakukan Swab PCR dan hasilnya dilaporkan pada atasan \r\n       dan apabila sudah dinyatakan negatif, maka yang bersangkutan dapat bekerja \r\n        kembali seperti biasa. \r\n\r\n   B.Bisa Melakukan swab PCR secara mandiri (biaya pribadi, tidak ditanggung kantor) \r\n       untuk memastikan tidak terpapar covid-19\r\n       #Apabila hasil dari swab PCR negatif bisa masuk kantor seperti biasa\r\n          *Biaya Swab PCR dengan hasil negatif akan ditanggung oleh karyawan \r\n            yang bersangkutan sebesar 50% dan oleh perusahaan sebesar 50% \r\n            (sertakan bukti hasil swab dan nota swab kepada atasan)\r\n          *Pembayaran swab PCR boleh dicicil ke perusahaan sebanyak maksimal 5x cicil \r\n             dari potong gaji.\r\n       #Bila hasil positif maka swab pcr tidak ditanggung kantor (harus ditanggung pribadi)\r\n\r\n\r\n2.Apabila karyawan L\'VIORS pergi ke luar kota untuk keperluan pribadi lebih dari 1malam \r\n   (pulang kampung/liburan) maka wajib lapor ke atasan masing- masing divisi dan\r\n    wajib melakukan swab antigen saat baru datang serta 1minggu post swab awal. \r\n    Biaya 1x swab antigen khusus karyawan akan dikenakan biaya Rp.100.000,-/kali swab.\r\n\r\n\r\nBerlaku sejak 16 Januari 2021\r\nBerlaku di Klinik Kayoon, Beverly, Medan, Balikpapan, Jakarta, Pekanbaru\r\n', NULL, '2021-01-16', 'robbin', NULL, NULL, '2021-03-01', '2021-12-31', 'true', 'MIT');
INSERT INTO `news` VALUES (4, NULL, 'Perubahan Metode Pembayaran Gaji', '\'Dengan Hormat,\r\nBersama ini kami memberitahukan bahwa seluruh karyawan LVIORS diharuskan untuk membuka rekening BCA/MANDIRI atas nama pribadi, hal ini diperuntukan untuk penerimaan gaji bulanan dari Perusahaan.\r\n\r\nKetentuan ini berlaku untuk gaji Januari 2021, mohon untuk dapat mengumpulkan nomor rekening ke atasan terkait kemudian dirangkum dan diberikan ke Sisca (keuangan).', NULL, '2021-01-02', 'robbin', NULL, NULL, '2021-01-01', '2021-12-31', 'true', 'MIT');
INSERT INTO `news` VALUES (5, 'SB2/2101/IMO/0014', 'Aturan Swab Lk Lp', '\'Dengan Hormat,\r\nBersama ini kami memberitahukan bahwa untuk dokter,terapis dan marketing yang bertugas di lk lp:\r\n1.Saat keberangkatan wajib membawa surat swab antigen dari klinik\r\n2.Dikarenakan surat hanya berlaku 3hari, maka jadwal lk lp yang berlangsung lebih dari 3hari, tim harus melakukan swab diluar/ di kota tersebut sebagai syarat untuk kembali ke klinik/kota asal (terkait pemeriksaan di bandara dll)--> apabila terdapat peraturan wajib menunjukkan surat swab dari pihak yang bersangkutan. Dan biaya swab tim akan dibebankan ke perusahaan (sertakan hasil dan bukti/nota swab ke kantor).\r\nNB: Jika ada distributor yang memberi fasilitas swab free, maka tidak ada yg di klaim kan ke perusahan\r\n3.Jika ada tugas Lk Lp yang tidak sampai 3hari, maka sekembalinya tim tersebut dari lk lp maka wajib dilakukan swab di klinik (marketing nya wajib konfirmasi dengan dr.maria paling lambat h-3hari keberangkatan)\r\n\r\nBerlaku untuk seluruh jadwal lk/lp\r\nBerlaku sejak 19 Januari 2021', NULL, '2021-01-19', 'robbin', NULL, NULL, '2021-01-01', '2021-12-31', 'true', 'MIT');
